import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import './ExcelTable.css'; // Optional for styling

const ExcelTable = () => {
  const [columns, setColumns] = useState([]);
  const [rows, setRows] = useState([]);

  // Handle Excel file upload and parse the data
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      const binaryStr = event.target.result;
      const workbook = XLSX.read(binaryStr, { type: 'binary' });
      const worksheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[worksheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 }); // Getting data as 2D array

      const cols = jsonData[0]; // The first row as headers
      const data = jsonData.slice(1); // Remaining rows as data

      setColumns(cols);
      setRows(data);
    };

    reader.readAsBinaryString(file);
  };

  return (
    <div>
      <div className="gpt3__whatgpt3 section__margin" id="wgpt3">
      <h2>Upload Excel File</h2>
      <input type="file" onChange={handleFileUpload} accept=".xlsx, .xls" />

      {rows.length > 0 && (
        <table className="excel-table">
          <thead>
            <tr>
              {columns.map((col, index) => (
                <th key={index}>{col}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {row.map((cell, cellIndex) => (
                  <td key={cellIndex}>{cell}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>



      </div>
      
  );
};

export default ExcelTable;
