import React, { forwardRef, useEffect, useState } from 'react';
import './cta.css';

const CTA = forwardRef((props, ref) => {
  const [ctaData, setCtaData] = useState(null); // Updated to single JSON object

  // Fetch data from Node.js server endpoint
  useEffect(() => {
    fetch('http://localhost:5001/upload-json/ctaData.json')  // Adjust URL as needed
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => setCtaData(data.data)) // Access the data inside the response
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  if (!ctaData) {
    return <div>Loading...</div>;
  }

  const CTATile = ({ title, description }) => {
    const handleButtonClick = () => {
      alert(`Clicked on ${title}`);
    };

    return (
      <div className="cta__tile" onClick={handleButtonClick}>
        <h3>{title}</h3>
        <p>{description}</p>
      </div>
    );
  };

  return (
    <div className="gpt3__cta section__margin" ref={ref}>
      <h1>Aditya</h1>
      <h1>Visions</h1>
      <div className="cta__container">
        <CTATile title={ctaData.title} description={ctaData.description} />
      </div>
    </div>
  );
});

export default CTA;
