import React from 'react';
import './possibility.css';

// Competitors Data
const competitorsData = [
  {
    name: 'Vijay Sales',
    imgUrl: 'path_to_vijay_sales_logo', // Replace with actual image URL
    jsonFile: 'vijay_sales_data.json'
  },
  {
    name: 'Reliance Digital',
    imgUrl: 'path_to_reliance_digital_logo', // Replace with actual image URL
    jsonFile: 'reliance_digital_data.json'
  },
  {
    name: 'Aditya Vision',
    imgUrl: 'path_to_aditya_vision_logo', // Replace with actual image URL
    jsonFile: 'aditya_vision_data.json'
  },
  {
    name: 'Bajaj Electronics',
    imgUrl: 'path_to_bajaj_electronics_logo', // Replace with actual image URL
    jsonFile: 'bajaj_electronics_data.json'
  }
];

// Competitor Tile Component
const CompetitorTile = ({ name, imgUrl, jsonFile }) => {
  const handleButtonClick = () => {
    fetch(jsonFile)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        alert(`Opened data for ${name}`);
      })
      .catch((error) => console.error('Error loading JSON:', error));
  };

  return (
    <div className="competitor__tile">
      <img src={imgUrl} alt={name} className="competitor__tile-image" />
      <h3>{name}</h3>
      <button onClick={handleButtonClick}>Open JSON</button>
    </div>
  );
};

// Main Possibility Component
const Possibility = () => {
  return (
    <div className="gpt3__possibility section__padding" id='possibility'>
      <div className="gpt3__possibility-heading">
        <h1 id="font">The Competitors of Croma</h1>
        <p>Here are some of the leading competitors in the electronics market.</p>
      </div>
      <div className="competitors__container">
        {competitorsData.map((competitor, index) => (
          <CompetitorTile 
            key={index} 
            name={competitor.name} 
            imgUrl={competitor.imgUrl} 
            jsonFile={competitor.jsonFile} 
          />
        ))}
      </div>
    </div>
  );
};

export default Possibility;
