import React, { forwardRef, useEffect, useState } from 'react';
import './cta.css';

const CTA = forwardRef((props, ref) => {
  const [ctaData, setCtaData] = useState(null);
  const [error, setError] = useState(false);

  // Fetch data from Flask server, fallback to local JSON if it fails
  useEffect(() => {
    fetch('http://localhost:5002/get-llama-json', {
      method: 'GET',  // Should be GET or POST depending on Flask server setup
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch data from Flask server');
        }
        return response.json();
      })
      .then((data) => {
        setCtaData(data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setError(true);

        // Fallback to local JSON data from public directory
        fetch('/ctaData.json') // Fetch from the public folder
          .then((response) => {
            if (!response.ok) {
              throw new Error('Failed to fetch local data');
            }
            return response.json();
          })
          .then((localData) => setCtaData(localData))
          .catch((localError) =>
            console.error('Error fetching fallback local data:', localError)
          );
      });
  }, []);

  if (!ctaData) {
    return (
      <div className="gpt3__cta section__margin" ref={ref}>
        <h1>Aditya</h1>
        <h1>Visions</h1>
        <div>Loading...</div> {/* Handle loading state */}
      </div>
    );
  }

  const CTATile = ({ title, description }) => {
    const handleButtonClick = () => {
      alert(`Clicked on ${title}`);
    };

    return (
      <div className="cta__tile" onClick={handleButtonClick}>
        <h3>{title}</h3>
        <p>{description}</p>
      </div>
    );
  };

  const ParentTile = ({ parentTitle, children }) => {
    return (
      <div className="cta__parent-tile">
        <h2 className="parent-title">{parentTitle}</h2>
        {children}
      </div>
    );
  };

  return (
    <div className="gpt3__cta section__margin" ref={ref}>
      <h1>Aditya</h1>
      <h1>Visions</h1>
      <div className="gpt3__cta-content"></div>
      <div className="cta__container">
        {ctaData && (
          <ParentTile parentTitle={ctaData.parentTitle}>
            <CTATile title={ctaData.title} description={ctaData.description} />
          </ParentTile>
        )}
      </div>
    </div>
  );
});

export default CTA;
